INSERT INTO `playerchoice_response` (`ChoiceId`, `ResponseId`, `Index`, `ChoiceArtFileId`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES (352, 956, 2, 1851098, 'Duskwood', 'Join the Night Watch', 'Everlasting night falls over Darkshire, with man-eating wolves and wild worgen threatening the people that live there.$b$bThey need help if they are to survive against the threats that lurk in the shadows.', '', 26124);
INSERT INTO `playerchoice_response` (`ChoiceId`, `ResponseId`, `Index`, `ChoiceArtFileId`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES (352, 972, 1, 1851118, 'Nagrand', 'Stop the Murkblood', 'The Kurenai of Telaar have been framed for the destruction of an orcish outpost by the Murkblood tribe!\n\nHelp the Kurenai restore their reputation and find peace in Outland.', '', 26124);
INSERT INTO `playerchoice_response` (`ChoiceId`, `ResponseId`, `Index`, `ChoiceArtFileId`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES (352, 1429, 0, 1851120, 'Broken Shore', 'Fight the Legion', 'The Burning Legion has returned to Azeroth!', '', 26124);
INSERT INTO `playerchoice_response` (`ChoiceId`, `ResponseId`, `Index`, `ChoiceArtFileId`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES (352, 939, 2, 1851107, 'Stonetalon Mountains', 'Travel to Stardust Spire', 'The Horde has been relentless in their conquest of Ashenvale.\n\nNow is the time for a decisive counterattack against their hold on Stonetalon Mountains.', '', 26124);
INSERT INTO `playerchoice_response` (`ChoiceId`, `ResponseId`, `Index`, `ChoiceArtFileId`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES (352, 957, 2, 1851113, 'Wetlands', 'Help the Survey Team', 'The enemies of Ironforge seek to exploit the destruction of the Stonewrought Dam.$b$bHelp the survey team deployed to the region understand where our weaknesses exist and how we can help our dwarven allies rebuild.', '', 26124);
INSERT INTO `playerchoice_response` (`ChoiceId`, `ResponseId`, `Index`, `ChoiceArtFileId`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES (352, 970, 2, 1851122, 'Zangarmarsh', 'Assist the Expedition', 'The Cenarion Expedition is currently studying the exotic flora and fauna in the marshes of Zangarmarsh.\n\nHelping them with their mission will also help the Alliance gain a better understanding of Outland.', '', 26124);
INSERT INTO `playerchoice_response` (`ChoiceId`, `ResponseId`, `Index`, `ChoiceArtFileId`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES (352, 976, 2, 1851148, 'Borean Tundra', 'Enlist with the Expedition', 'The war against the Scourge has begun!\n\nBecome an enlisted soldier with the Valiance Expedition. Help the Alliance achieve victory on the western shores of Northrend!', '', 26124);
INSERT INTO `playerchoice_response` (`ChoiceId`, `ResponseId`, `Index`, `ChoiceArtFileId`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES (352, 977, 2, 1851153, 'Howling Fjord', 'Battle the Vrykul', 'The port town of Valgarde is located in the shadows of Utgarde Keep, and is under constant threat from the vrykul that spill forth from its halls.\n\nOur victory against the Scourge begins with defeating the vrykul and controlling the eastern shores of Northrend!', '', 26124);
INSERT INTO `playerchoice_response` (`ChoiceId`, `ResponseId`, `Index`, `ChoiceArtFileId`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES (352, 961, 2, 1851096, 'The Cape of Stranglethorn', 'Discover Hidden Artifacts', 'The Explorer\'s League is looking for artifacts that may have been unearthed after the Shattering.$b$bHelp them uncover whatever secrets the lands hold.', '', 26124);
INSERT INTO `playerchoice_response` (`ChoiceId`, `ResponseId`, `Index`, `ChoiceArtFileId`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES (352, 960, 2, 1851103, 'The Hinterlands', 'Help the Wildhammer', 'The Wildhammer clan needs our help protecting their homeland against the Horde.$b$bShow them that their kindness is not forgotten by the Alliance.', '', 26124);
INSERT INTO `playerchoice_response` (`ChoiceId`, `ResponseId`, `Index`, `ChoiceArtFileId`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES (352, 989, 2, 1851133, 'The Jade Forest', 'Report to Stormwind Keep', 'A naval battle between the Alliance and Horde have stranded some of our people on an unknown landmass.$b$bBring them back home.', '', 26124);
INSERT INTO `playerchoice_response` (`ChoiceId`, `ResponseId`, `Index`, `ChoiceArtFileId`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES (352, 1014, 2, 1851147, 'Tanaan Jungle', 'Enter the Dark Portal', 'The Iron Horde are invading Azeroth!\n\nWe must stop them to save our world. For Azeroth!', '', 26124);
INSERT INTO `playerchoice_response` (`ChoiceId`, `ResponseId`, `Index`, `ChoiceArtFileId`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES (352, 978, 2, 1851151, 'Dragonblight', 'Travel to Dragonblight', 'A direct route into Icecrown exists in the Light-forsaken land of Dragonblight.\n\nSlow the Scourge and give our forces time to build an offensive to lay siege to the Wrathgate.', '', 26124);
INSERT INTO `playerchoice_response` (`ChoiceId`, `ResponseId`, `Index`, `ChoiceArtFileId`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES (352, 979, 2, 1851152, 'Grizzly Hills', 'Assist the Military', 'The Horde are sending their forces to pillage our storehouses, raid our encampments, and strip the land of its resources.\n\nFind what we need to break our stalemate with the Horde.', '', 26124);
INSERT INTO `playerchoice_response` (`ChoiceId`, `ResponseId`, `Index`, `ChoiceArtFileId`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES (352, 978, 1, 1851151, 'Dragonblight', 'Travel to Dragonblight', 'A direct route into Icecrown exists in the Light-forsaken land of Dragonblight.\n\nSlow the Scourge and give our forces time to build an offensive to lay siege to the Wrathgate.', '', 26124);
