INSERT INTO `playerchoice_response` (`ChoiceId`, `ResponseId`, `Index`, `ChoiceArtFileId`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES (352, 945, 2, 1851109, 'Tanaris', 'Convince Gadgetzan', 'The Steamwheedle Cartel is one of the most successful goblin cartels, and we need to establish an Alliance presence in their capital city.\n\nPersuade Marin Noggenfogger that Gadgetzan should side with the Alliance.', '', 26124);
INSERT INTO `playerchoice_response` (`ChoiceId`, `ResponseId`, `Index`, `ChoiceArtFileId`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES (352, 990, 2, 1851139, 'Valley of the Four Winds', 'Travel to the Valley', 'The tale of two pandaren and their exploits around Pandaria is echoed by all that have been to the Valley of the Four Winds.\n\nLearn from the Stormstouts what makes Pandaria special.', '', 26124);
