INSERT INTO `playerchoice_response` (`ChoiceId`, `ResponseId`, `Index`, `ChoiceArtFileId`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES (352, 961, 0, 1851096, 'The Cape of Stranglethorn', 'Discover Hidden Artifacts', 'The Explorer\'s League is looking for artifacts that may have been unearthed after the Shattering.$b$bHelp them uncover whatever secrets the lands hold.', '', 26124);
INSERT INTO `playerchoice_response` (`ChoiceId`, `ResponseId`, `Index`, `ChoiceArtFileId`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES (352, 957, 0, 1851113, 'Wetlands', 'Help the Survey Team', 'The enemies of Ironforge seek to exploit the destruction of the Stonewrought Dam.$b$bHelp the survey team deployed to the region understand where our weaknesses exist and how we can help our dwarven allies rebuild.', '', 26124);
INSERT INTO `playerchoice_response` (`ChoiceId`, `ResponseId`, `Index`, `ChoiceArtFileId`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES (352, 962, 0, 1851112, 'Western Plaguelands', 'Defeat the Forsaken', 'The Forsaken have launched a counterattack to cut off Alliance forces and claim Andorhal for themselves!$b$bStop the Scourge, defeat the Horde, and reclaim Andorhal for the Alliance!', '', 26124);
INSERT INTO `playerchoice_response` (`ChoiceId`, `ResponseId`, `Index`, `ChoiceArtFileId`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES (352, 964, 0, 1851093, 'Badlands', 'Gab with Gek', 'The goblins of Fuselight are looking for adventurers to tame the wilderness surrounding their newly built town.$b$bDeal with the threats to Fuselight, and the Alliance\'s relationship with the Steamwheedle Cartel will flourish.', '', 26124);
INSERT INTO `playerchoice_response` (`ChoiceId`, `ResponseId`, `Index`, `ChoiceArtFileId`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES (352, 965, 0, 1851105, 'Searing Gorge', 'Investigate the Dwarves', 'Ragnaros\' Dark Iron dwarves are organizing, and appear poised to attack Alliance controlled territories!$b$bWork with the Thorium Brotherhood to uncover their motives and end their incursion.', '', 26124);
INSERT INTO `playerchoice_response` (`ChoiceId`, `ResponseId`, `Index`, `ChoiceArtFileId`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES (352, 966, 0, 1851094, 'Blasted Lands', 'Stop the Horde', 'Our forces at Nethergarde Keep need reinforcements to hold back the Horde.$b$bTurn the tide of battle in favor of the Alliance!', '', 26124);
INSERT INTO `playerchoice_response` (`ChoiceId`, `ResponseId`, `Index`, `ChoiceArtFileId`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES (352, 967, 0, 1851095, 'Burning Steppes', 'Speak with Keeshan', 'The Blackrock clan is poised to attack Redridge and other neighboring lands. The aggression of the Blackrock orcs cannot be left unchecked.$b$bWork with Keeshan to stop the Blackrock from advancing across Eastern Kingdoms.\n', '', 26124);
INSERT INTO `playerchoice_response` (`ChoiceId`, `ResponseId`, `Index`, `ChoiceArtFileId`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES (352, 968, 0, 1851100, 'Eastern Plaguelands', 'Travel with Fiona', 'A group of adventurers are traveling to Light\'s Hope Chapel to enlist with the Argent Crusade.$b$bThe caravan leader, Fiona, needs help reaching their destination safely.', '', 26124);
INSERT INTO `playerchoice_response` (`ChoiceId`, `ResponseId`, `Index`, `ChoiceArtFileId`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES (352, 970, 0, 1851122, 'Zangarmarsh', 'Assist the Expedition', 'The Cenarion Expedition is currently studying the exotic flora and fauna in the marshes of Zangarmarsh.\n\nHelping them with their mission will also help the Alliance gain a better understanding of Outland.', '', 26124);
INSERT INTO `playerchoice_response` (`ChoiceId`, `ResponseId`, `Index`, `ChoiceArtFileId`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES (352, 969, 0, 1851116, 'Hellfire Peninsula', 'Travel to Outland', 'The Dark Portal has reopened and the Burning Legion is now positioned to attack Azeroth yet again.\n\nStop the demon filth from taking control of the Dark Portal and cutting off supplies to our forces in Hellfire Peninsula.', '', 26124);
INSERT INTO `playerchoice_response` (`ChoiceId`, `ResponseId`, `Index`, `ChoiceArtFileId`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES (352, 971, 0, 1851121, 'Terokkar Forest', 'Assist Allerian Stronghold', 'The forest to the south of Shattrath City is both beautiful and dangerous.\n\nThe people in Allerian Stronghold will need all the help they can get to survive in Terokkar Forest.', '', 26124);
INSERT INTO `playerchoice_response` (`ChoiceId`, `ResponseId`, `Index`, `ChoiceArtFileId`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES (352, 972, 0, 1851118, 'Nagrand', 'Stop the Murkblood', 'The Kurenai of Telaar have been framed for the destruction of an orcish outpost by the Murkblood tribe!\n\nHelp the Kurenai restore their reputation and find peace in Outland.', '', 26124);
INSERT INTO `playerchoice_response` (`ChoiceId`, `ResponseId`, `Index`, `ChoiceArtFileId`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES (352, 977, 0, 1851153, 'Howling Fjord', 'Battle the Vrykul', 'The port town of Valgarde is located in the shadows of Utgarde Keep, and is under constant threat from the vrykul that spill forth from its halls.\n\nOur victory against the Scourge begins with defeating the vrykul and controlling the eastern shores of Northrend!', '', 26124);
INSERT INTO `playerchoice_response` (`ChoiceId`, `ResponseId`, `Index`, `ChoiceArtFileId`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES (352, 972, 2, 1851118, 'Nagrand', 'Stop the Murkblood', 'The Kurenai of Telaar have been framed for the destruction of an orcish outpost by the Murkblood tribe!\n\nHelp the Kurenai restore their reputation and find peace in Outland.', '', 26124);
INSERT INTO `playerchoice_response` (`ChoiceId`, `ResponseId`, `Index`, `ChoiceArtFileId`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES (352, 976, 0, 1851148, 'Borean Tundra', 'Enlist with the Expedition', 'The war against the Scourge has begun!\n\nBecome an enlisted soldier with the Valiance Expedition. Help the Alliance achieve victory on the western shores of Northrend!', '', 26124);
INSERT INTO `playerchoice_response` (`ChoiceId`, `ResponseId`, `Index`, `ChoiceArtFileId`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES (352, 978, 0, 1851151, 'Dragonblight', 'Travel to Dragonblight', 'A direct route into Icecrown exists in the Light-forsaken land of Dragonblight.\n\nSlow the Scourge and give our forces time to build an offensive to lay siege to the Wrathgate.', '', 26124);
INSERT INTO `playerchoice_response` (`ChoiceId`, `ResponseId`, `Index`, `ChoiceArtFileId`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES (352, 979, 0, 1851152, 'Grizzly Hills', 'Assist the Military', 'The Horde are sending their forces to pillage our storehouses, raid our encampments, and strip the land of its resources.\n\nFind what we need to break our stalemate with the Horde.', '', 26124);
INSERT INTO `playerchoice_response` (`ChoiceId`, `ResponseId`, `Index`, `ChoiceArtFileId`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES (352, 989, 0, 1851133, 'The Jade Forest', 'Report to Stormwind Keep', 'A naval battle between the Alliance and Horde have stranded some of our people on an unknown landmass.$b$bBring them back home.', '', 26124);
INSERT INTO `playerchoice_response` (`ChoiceId`, `ResponseId`, `Index`, `ChoiceArtFileId`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES (352, 990, 0, 1851139, 'Valley of the Four Winds', 'Travel to the Valley', 'The tale of two pandaren and their exploits around Pandaria is echoed by all that have been to the Valley of the Four Winds.\n\nLearn from the Stormstouts what makes Pandaria special.', '', 26124);
INSERT INTO `playerchoice_response` (`ChoiceId`, `ResponseId`, `Index`, `ChoiceArtFileId`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES (352, 991, 0, 1851134, 'Krasarang Wilds', 'Travel to Krasarang', 'The pandaren of Krasarang Wilds have been beset by unrelenting rain and a general malaise has fallen over the land.\n\nAssist the denizens of the Wilds, and find out what is afflicting the otherwise jovial pandaren.', '', 26124);
INSERT INTO `playerchoice_response` (`ChoiceId`, `ResponseId`, `Index`, `ChoiceArtFileId`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES (352, 1014, 0, 1851147, 'Tanaan Jungle', 'Enter the Dark Portal', 'The Iron Horde are invading Azeroth!\n\nWe must stop them to save our world. For Azeroth!', '', 26124);
