INSERT INTO `playerchoice_response` (`ChoiceId`, `ResponseId`, `Index`, `ChoiceArtFileId`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES (352, 939, 1, 1851107, 'Stonetalon Mountains', 'Travel to Stardust Spire', 'The Horde has been relentless in their conquest of Ashenvale.\n\nNow is the time for a decisive counterattack against their hold on Stonetalon Mountains.', '', 26124);
