INSERT INTO `playerchoice_response` (`ChoiceId`, `ResponseId`, `Index`, `ChoiceArtFileId`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES (352, 949, 2, 1851106, 'Silithus', 'Travel to Silithus', 'The Twilight\'s Hammer and silithid roam the desert of Silithus, terrorizing the Cenarion Circle and adventurers alike.\n\nHelp our allies stop the Twilight\'s Hammer and root out the silithid from their hives.', '', 26124);
INSERT INTO `playerchoice_response` (`ChoiceId`, `ResponseId`, `Index`, `ChoiceArtFileId`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES (352, 944, 2, 1851099, 'Dustwallow Marsh', 'Assist Theramore', 'Theramore is a proud Alliance city in the heart of Horde territory.\n\nThreats from within the city and outside its walls seek to diminish its status on Kalimdor.', '', 26124);
INSERT INTO `playerchoice_response` (`ChoiceId`, `ResponseId`, `Index`, `ChoiceArtFileId`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES (352, 938, 2, 1851070, 'Ashenvale', 'Assist the Sentinels', 'The Horde has sent its forces to Ashenvale, seeking to rout out the night elves that live in the forest.\n\nStop the Horde\'s desecration of the night elf homeland.', '', 26124);
