SET @column_exists := (
    SELECT COUNT(*)
    FROM INFORMATION_SCHEMA.COLUMNS
    WHERE TABLE_SCHEMA = DATABASE()
      AND TABLE_NAME = 'playerchoice_response_reward'
      AND COLUMN_NAME = 'SpellID'
);

SET @query := IF(@column_exists = 0,
    'ALTER TABLE `playerchoice_response_reward` ADD COLUMN `SpellID` INT(10) UNSIGNED NOT NULL DEFAULT 0 AFTER `Xp`;',
    'SELECT "Column `SpellID` already exists";'
);

PREPARE stmt FROM @query;
EXECUTE stmt;
DEALLOCATE PREPARE stmt;
	
